/* Noch zu Erledigen: 
- Button um Übersichtskarte einzublenden
- set png as pointlayer src
- change/swap basemap onClick -> stamenLayer Switcher
- AgFeatureGrid fixen

*/
import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as PropTypes from "prop-types";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";
import OlMap from "ol/Map";
import OlView from "ol/View";
import OlVector from "ol/layer/Vector";
import OlVectorSource from "ol/source/Vector";
import GeoJSON from "ol/format/GeoJSON";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
import OlSourceStamen from "ol/source/Stamen";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";
import OlLayerGroup from "ol/layer/Group";

import { Drawer, Icon } from "antd";
import {
  SimpleButton,
  MapComponent,
  AgFeatureGrid,
  ZoomToExtentButton,
  GeoLocationButton,
  LayerTransparencySlider,
  ToggleGroup,
  ToggleButton,
  NominatimSearch,
  Window,
  MeasureButton,
  LayerTree
} from "@terrestris/react-geo";

import TextField from "@material-ui/core/TextField";

import OlFormatGeoJSON from "ol/format/GeoJSON";
import Select from "ol/interaction/Select";
import { ZoomSlider } from "ol/control";
import TileLayer from "ol/layer/Tile";
import XYZ from "ol/source/XYZ";
import OSM from "ol/source/OSM";
import View from "ol/View";
import { defaults as defaultControls, FullScreen } from "ol/control";
import { fromLonLat } from "ol/proj";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";
import getlocation from "../img/getlocation.png";
import haus from "../img/haus.jpg";
import search from "../img/search.png";
import toilets from "../img/unisex.png";
import ruler from "../img/ruler.png";
import layerpng from "../img/layer.png";

import buildingsData from "../campusGisBuildings.geojson";
import klinikumBuildings from "../UniklinikBuildings.geojson";
import toiletsPoints from "../toilets.geojson";
import testdata from "../testdata.json";

import RundbauGrundriss from "../RundbauGrundriss.geojson";
import EtageEinsRäume from "../EtageEinsRäume.geojson";
import EtageZweiRäume from "../EtageZweiRäume.geojson";
import EtageEinsGang from "../EtageEinsGang.geojson";

import { Style, Fill, Stroke } from "ol/style";

const appId = "Your HERE Maps appId from https://developer.here.com/";
const appCode = "Your HERE Maps appCode from https://developer.here.com/";

const hereLayers = [
  {
    base: "base",
    type: "maptile",
    scheme: "normal.day",
    app_id: appId,
    app_code: appCode
  },
  {
    base: "base",
    type: "maptile",
    scheme: "normal.day.transit",
    app_id: appId,
    app_code: appCode
  },
  {
    base: "base",
    type: "maptile",
    scheme: "pedestrian.day",
    app_id: appId,
    app_code: appCode
  },
  {
    base: "aerial",
    type: "maptile",
    scheme: "terrain.day",
    app_id: appId,
    app_code: appCode
  },
  {
    base: "aerial",
    type: "maptile",
    scheme: "satellite.day",
    app_id: appId,
    app_code: appCode
  },
  {
    base: "aerial",
    type: "maptile",
    scheme: "hybrid.day",
    app_id: appId,
    app_code: appCode
  }
];

const urlTpl =
  "https://{1-4}.{base}.maps.cit.api.here.com" +
  "/{type}/2.1/maptile/newest/{scheme}/{z}/{x}/{y}/256/png" +
  "?app_id={app_id}&app_code={app_code}";

function createUrl(tpl, layerDesc) {
  return tpl
    .replace("{base}", layerDesc.base)
    .replace("{type}", layerDesc.type)
    .replace("{scheme}", layerDesc.scheme)
    .replace("{app_id}", layerDesc.app_id)
    .replace("{app_code}", layerDesc.app_code);
}

var layers = [];
var i, ii;
for (i = 0, ii = hereLayers.length; i < ii; ++i) {
  var layerDesc = hereLayers[i];
  layers.push(
    new TileLayer({
      visible: false,
      preload: Infinity,
      source: new XYZ({
        url: createUrl(urlTpl, layerDesc),
        attributions:
          "Map Tiles &copy; " +
          new Date().getFullYear() +
          " " +
          '<a href="http://developer.here.com">HERE</a>'
      })
    })
  );
}

var select = document.getElementById("layer-select");
function onChange() {
  var scheme = select.value;
  for (var i = 0, ii = layers.length; i < ii; ++i) {
    layers[i].setVisible(hereLayers[i].scheme === scheme);
  }
}

if (select) {
  select.addEventListener("change", onChange);
  onChange();
}

const center = [771105.02, 6608382.01]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  controls: defaultControls().extend([new FullScreen()]),
  view: new OlView({
    center: center,
    zoom: 0.9,
    maxResolution: 7
  }),
  layers: layers
});

var zoomslider = new ZoomSlider();
map.addControl(zoomslider);

// adding GeoJSON to the map (buildings)
const vector = new OlVector({
  source: new OlVectorSource({
    url: buildingsData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#354d60"
    })
    /* the following few lines of code will make the map buggy
    stroke: new Stroke({
      color: "black",
      width: 0.2
    })
    */
  }),
  minResolution: 0.4
});

map.addLayer(vector);

// adding GeoJSON to the map (rooms)
const vectorTwo = new OlVector({
  source: new OlVectorSource({
    url: RundbauGrundriss,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "grey"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(vectorTwo);

// adding GeoJSON to the map (Gang)
const GangRundbau = new OlVector({
  source: new OlVectorSource({
    url: EtageEinsGang,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "grey"
    }),
    stroke: new Stroke({
      color: "red",
      width: 5
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(GangRundbau);

// adding GeoJSON to the map (rooms)
const vectorThree = new OlVector({
  source: new OlVectorSource({
    url: klinikumBuildings,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#5493C4"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  minResolution: 0.4
});

map.addLayer(vectorThree);

// adding GeoJSON to the map (EtageEinsRäume)
const EtageEinsRäumeLayer = new OlVector({
  source: new OlVectorSource({
    url: EtageEinsRäume,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "blue"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(EtageEinsRäumeLayer);

// adding GeoJSON to the map (EtageZweiRäume)
const EtageZweiRäumeLayer = new OlVector({
  source: new OlVectorSource({
    url: EtageZweiRäume,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "blue"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(EtageZweiRäumeLayer);

// adding GeoJSON to the map (toilets)
const toiletsPointsLayer = new OlVector({
  source: new OlVectorSource({
    url: toiletsPoints,
    format: new GeoJSON()
  })
});

map.addLayer(toiletsPointsLayer);

map.on("postcompose", map.updateSize);

class Ebenen extends Component {
  state = {
    show: true,
    showTwo: true,
    color: false
  };

  hideLayer = () => {
    EtageEinsRäumeLayer.setVisible(false);
  };

  showLayer = () => {
    EtageEinsRäumeLayer.setVisible(true);
  };

  hideLayerTwo = () => {
    EtageZweiRäumeLayer.setVisible(false);
  };

  showLayerTwo = () => {
    EtageZweiRäumeLayer.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  toggleTwo = () =>
    this.setState(currentState => ({ showTwo: !currentState.showTwo }));

  render() {
    return (
      <div id='ContainerEbenen'>
        <button className='EbenenBtn' onClick={this.toggleTwo}>
          {this.state.showTwo ? this.hideLayerTwo() : this.showLayerTwo()}Etage
          2
        </button>

        <button className='EbenenBtn' id='button' onClick={this.toggle}>
          {this.state.show ? this.hideLayer() : this.showLayer()} Etage 1
        </button>
      </div>
    );
  }
}

class Displaydata extends Component {
  render() {
    return (
      <div id='displayData'>
        <div id='rowOne' className='rowDisplayData'>
          <p>Gebäudenummer</p>
          <p>Name</p>
          <p>Klinikum/Universität</p>
          <p>Straße</p>
        </div>

        {/* the clicked table rows data from Buildings.js should apear here */}
        <div id='rowTwo' className='rowDisplayData'>
          <p id='Gebäudenummer'></p>
          <p id='Name'></p>
          <p id='Art'></p>
          <p id='Straße'></p>
        </div>
      </div>
    );
  }
}

export class SearchForm extends Component {
  render() {
    return (
      <div>
        <div id='SearchForm'>
          <form noValidate autoComplete='off'>
            <TextField
              id='outlined-basic'
              label='Outlined'
              variant='outlined'
            />
            <Button></Button>
          </form>
        </div>
      </div>
    );
  }
}

// when the user moves the mouse, get the name property
// from each feature under the mouse and display it in <DisplayData />
function onMouseMove(browserEvent) {
  var coordinate = browserEvent.coordinate;
  var pixel = map.getPixelFromCoordinate(coordinate);
  var el = document.getElementById("Gebäudenummer");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("GEBNR") + "<br>";
  });

  var el = document.getElementById("Name");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("NAME") + "<br>";
  });

  var el = document.getElementById("Straße");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Straße") + "<br>";
  });

  var el = document.getElementById("Art");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Klinikum/Universität") + "<br>";
  });
}
map.on("click", onMouseMove);

// AgFeatureGrid

const format = new OlFormatGeoJSON();
const features = format.readFeatures(testdata);

// An array of numbers representing an extent: [minx, miny, maxx, maxy].
const extent1 = [988948, 1584461, 50935173, 6953101];

// map class with map component, DisplayData Class, Simple Button and Drawer
class Map extends Component {
  state = { visible: false, show: false, showTwo: false };

  toggleDrawer = () => {
    this.setState({ visible: !this.state.visible });
  };

  hideLayer = () => {
    vector.setVisible(false);
  };

  showLayer = () => {
    vector.setVisible(true);
  };

  hideLayerTwo = () => {
    vectorThree.setVisible(false);
  };

  showLayerTwo = () => {
    vectorThree.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  toggleTwo = () =>
    this.setState(currentState => ({ showTwo: !currentState.showTwo }));

  render() {
    return (
      <div className='App'>
        {/* <SearchForm />*/}
        {/*<LayerSwitcher map={map} layers={layers} />*/}
        <Accordion>
          <Card id='LegendeLayerAccordion'>
            <Card.Header id='LegendeLayerHeader'>
              <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                <img id='layerpng' src={layerpng}></img>
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey='0'>
              <Card.Body>
                <div id='containerHintergrund'>
                  <span className='underline'>Hintergrund Karte</span>
                  <br></br>{" "}
                  <select id='layer-select'>
                    <option value='normal.day' selected>
                      Normal Day
                    </option>
                    <option value='normal.day.transit'>
                      Normal Day Transit
                    </option>
                    <option value='pedestrian.day'>Pedestrian Day</option>
                    <option value='terrain.day'>Terrain Day</option>
                    <option value='satellite.day'>Satellite Day</option>
                    <option value='hybrid.day'>Hybrid Day</option>
                  </select>
                </div>
                <div id='containerEbenen'>
                  <span className='underline'>Etagen</span>
                  <Ebenen />
                </div>
                <div id='containerLayer'>
                  <span className='underline'>Layer</span>
                  <div id='containerLayerGrid'>
                    <SimpleButton
                      onClick={this.toggle}
                      className='layerVisibilityBtn'
                    >
                      {this.state.showTwo
                        ? this.hideLayerTwo()
                        : this.showLayerTwo()}
                    </SimpleButton>
                    <div id='blue'></div>
                    <div className='LayerLegendeText'>Universität</div>
                  </div>
                  <LayerTransparencySlider
                    layer={vector}
                    className='transparencyVector'
                  />
                </div>

                <div id='containerLayer'>
                  <div id='containerLayerGrid'>
                    <SimpleButton
                      onClick={this.toggleTwo}
