import React, { Component } from "react";

class Buildings extends Component {
  constructor(props) {
    super(props); //since we are extending class Table so we have to use super in order to override Component class constructor
    this.state = {
      buildings: [
        {
          id: 1,
          Gebäudenummer: "1",
          Gebäudeart: "Klinikum",
          Name: "Klinik-Verwaltung",
          Straße: "Kerpener Str. 68",
          BarrierFree: "Yes",
          BarrierFreeToilet: "Yes"
        },
        {
          id: 2,
          Gebäudenummer: "2",
          Gebäudeart: "Klinikum",
          Name: "Wohnheim",
          Straße: "Gleuler Str. 88a"
        },
        {
          id: 3,
          Gebäudenummer: "2a",
          Gebäudeart: "Klinikum",
          Name: "Klinikum-Verwaltung",
          Straße: "Gleueler Str. 88a"
        }
      ]
    };
  }

  highlightOnMap = () => {
    this.props.history.push("/Map");
  };

  render() {
    //Whenever our class runs, render method will be called automatically, it may have already defined in the constructor behind the scene.
    return (
      <div>
        <h2 className='überschrift'>Gebäudeübersicht</h2>
        <h3 className='description'>
          Klicken Sie auf die gewünschte Zeile, um das ausgewählte Gebäude auf
          dem Lapeplan anzuzeigen
        </h3>
        <div id='container-table'>
          <table className='u-full-width'>
            <thead>
              <tr>
                <th>Id</th>
                <th>Gebäudenummer</th>
                <th>Klinikum/Universität</th>
                <th>Name/Nutzer</th>
                <th>Straße</th>
                <th>Barrierefreier Zugang</th>
                <th>Barrierefreie Toilette</th>
              </tr>
            </thead>
            <tbody>{this.renderTableData()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Buildings;
