import React, { Component } from "react";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";

import OlMap from "ol/Map";
import OlView from "ol/View";
import OlSize from "ol/size";
import OlVector from "ol/layer/Vector";
import OlVectorSource from "ol/source/Vector";
import GeoJSON from "ol/format/GeoJSON";

import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
import OlSourceWMTS from "ol/source/WMTS";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";

import { Drawer } from "antd";
import { SimpleButton, MapComponent } from "@terrestris/react-geo";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";
import Buildings from "./Buildings";

const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

const center = [772305.02, 6608382.01]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  view: new OlView({
    center: center,
    zoom: 14
  }),
  layers: [stamenLayer]
});

// trying to add the geoJSON to the map
const vector = new OlVector({
  source: new OlVectorSource({
    url: "./buildings.geojson",
    format: new GeoJSON()
  })
});

map.addLayer(vector);

map.on("postcompose", map.updateSize);

// displays selected buildings data (not programmed yet)
class Displaydata extends Component {
  render() {
    return (
      <div id='displayData'>
        <div id='rowOne' className='rowDisplayData'>
          <p>Hausnummer</p>
          <p>Klinikum/Universität</p>
          <p>Name</p>
          <p>Straße</p>
        </div>

        {/* the clicked table rows data from Buildings.js should apear here */}
        <div id='rowTwo' className='rowDisplayData'>
          <p id='Gebäudenummer'>{}</p>
          <p id='Art'></p>
          <p id='Name'></p>
          <p id='Straße'></p>
        </div>
      </div>
    );
  }
}

// map class with map component, DisplayData Class, Simple Button and Drawer
class Map extends Component {
  state = { visible: false };

  toggleDrawer = () => {
    this.setState({ visible: !this.state.visible });
  };

  render() {
    return (
      <div className='App'>
        {/* DisplayData at the bottom left */}
        <Displaydata />
        {/* MAP */}
        <MapComponent map={map} />

        {/* Button on the left -> opens menu bar on click*/}
        <SimpleButton
          style={{ position: "fixed", top: "66px", left: "45px" }}
          onClick={this.toggleDrawer}
          icon='bars'
        />

        {/* this is the menu bar */}
        <Drawer
          title='Menüleiste'
          placement='left'
          onClose={this.toggleDrawer}
          visible={this.state.visible}
          mask={false}
          width='60vh'
        >
          <input
            type='text'
            placeholder='Straßen- oder Gebäudename...'
            id='inputSearch'
          ></input>
          <input type='submit' value='Suchen' id='form-btn'></input>

          {/* this is the accordion within the menu bar */}
          <Accordion defaultActiveKey='0'>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Filterfunktionen
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>Hier werden Filterfunktionen angezeigt.</Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Gebäudeliste
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  Klicke <NavLink to='/Buildings'>hier</NavLink>, um zu der
                  Gebäudeübersicht zu gelangen.
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Links
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <ul>
                    <li>
                      <NavLink to='/Buildings' target='_blank'>
                        Gebäudeliste
                      </NavLink>
                    </li>
                    <li>Lagepläne der Uniklinik</li>
                    <li>Parken an der Universität</li>
                    <li>Liniennetzplan der KVB</li>
                    <li>Öffnungszeiten zentrale Gebäude</li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Folge der Universität zu Köln!
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <a
                    href='https://www.facebook.com/universitaetkoeln/'
                    target='_blank'
                    className='fa fa-facebook'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.instagram.com/uni_koeln/?hl=de'
                    target='_blank'
                    className='fa fa-instagram'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.youtube.com/user/UniversitaetzuKoeln'
                    target='_blank'
                    className='fa fa-youtube'
                    id='socialMediaBtn'
                  ></a>
                  <ul>
                    <li>
                      <a
                        href='https://www.uni-koeln.de/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Universität zu Köln
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://www.geographie.uni-koeln.de/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Geographisches Institut
                      </a>
                    </li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <img src={uniSchriftzug} alt='logo' id='logoSchriftzug' />
        </Drawer>
      </div>
    );
  }
}

export default Map;
