import React, { Component } from "react";
import parkingOeffentlich from "../img/parking-öffentlich.png";
import parkingUni from "../img/parking-uni.png";
import parkingKlinik from "../img/parking-klinik.png";
import wickelraum from "../img/wickelraum.png";
import stillraum from "../img/stillraum.png";
import elternKindRaum from "../img/eltern-kind-raum.png";
import kita from "../img/kita.png";
import spielplatz from "../img/spielplatz.png";
import unisex from "../img/unisex.png";

import { LayerTransparencySlider } from "@terrestris/react-geo";

const vector = new OlVector({
  source: new OlVectorSource({
    url: buildingsData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#354d60"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  minResolution: 0.4
});
// displays layer
class LegendeLayer extends Component {
  state = {
    show: false
  };

  hideLayer = () => {
    vector.setVisible(false);
  };

  showLayer = () => {
    vector.setVisible(true);
  };

  hideLayerThree = () => {
    vectorThree.setVisible(false);
  };

  showLayerThree = () => {
    vectorThree.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  render() {
    return (
      <div>
        <div id='gridLayerLegende'>
          <input type='radio' onClick={this.toggle} id='layerVisibilityBtn'>
            {this.state.show ? this.hideLayer() : this.showLayer()}
          </input>
          <div id='blue' onClick={this.showLayer}></div>
          <div className='LayerLegendeText'>Universität</div>
          <LayerTransparencySlider
            layer={vector}
            className='transparencyVector'
          />

          <input type='radio' onClick={this.toggle} id='layerVisibilityBtn'>
            {this.state.show ? this.hideLayerThree() : this.showLayerThree()}
          </input>
          <div id='red' onClick={this.showLayerThree}></div>
          <div className='LayerLegendeText'>Uniklinik</div>
          <LayerTransparencySlider
            layer={vectorThree}
            className='transparencyVector'
          />
        </div>
      </div>
    );
  }
}

export default LegendeLayer;
