import React, { Component } from "react";
import barrierfreeEntrance from "../img/barrierfreeEntrance.jpeg";
import bushaltestelle from "../img/bushaltestelle.svg";
import regionalerVerkehr from "../img/regionalerVerkehr.svg";

class Legende extends Component {
  state = {
    visible: false
  };

  hideLegende = () => {
    this.setState({
      visible: false
    });
  };

  showLegende = () => {
    this.setState({
      visible: true
    });
  };

  render() {
    return (
      <div>
        <div
          id='legende'
          style={{ display: this.state.visible ? "block" : "none" }}
        >
          <button onClick={this.hideLegende}>Legende ausblenden</button>
          <div id='legendeGrid'>
            <p className='gridFullWidth'>Parkpl√§tze</p>
            <img className='legendeLogo'></img>
            <p className='legendeText'>Barrierefreier Zugang</p>
            <img className='legendeLogo'></img>
            <p className='legendeText'>KvB-Haltestellen</p>
            <img className='legendeLogo'></img>
            <p className='legendeText'>regionaler Verkehr</p>

            <p className='gridFullWidth'>Familien-Campus-Plan</p>
          </div>
        </div>
        <button
          onClick={this.showLegende}
          id='legendeShow'
          style={{ display: this.state.visible ? "none" : "block" }}
        >
          Legende anzeigen
        </button>
      </div>
    );
  }
}

export default Legende;
