/* Noch zu Erledigen: 
- Button um Übersichtskarte einzublenden
- Button für vorige und folgende Kartenansicht


*/

import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as PropTypes from "prop-types";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";
import OlMap from "ol/Map";
import OlView from "ol/View";
import OlVector from "ol/layer/Vector";
import OlVectorSource from "ol/source/Vector";
import GeoJSON from "ol/format/GeoJSON";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
import OlSourceStamen from "ol/source/Stamen";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";
import { Drawer, Icon } from "antd";
import {
  SimpleButton,
  MapComponent,
  AgFeatureGrid,
  ZoomToExtentButton,
  GeoLocationButton,
  LayerTransparencySlider,
  ToggleGroup,
  ToggleButton,
  NominatimSearch,
  Window,
  MeasureButton
} from "@terrestris/react-geo";
import OlFormatGeoJSON from "ol/format/GeoJSON";
import Select from "ol/interaction/Select";
import { ZoomSlider } from "ol/control";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import View from "ol/View";
import { defaults as defaultControls, FullScreen } from "ol/control";
import { fromLonLat } from "ol/proj";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";
import getlocation from "../img/getlocation.png";
import haus from "../img/haus.jpg";
import search from "../img/search.png";
import toilets from "../img/unisex.png";

import buildingsData from "../campusGisBuildings.geojson";
import klinikumBuildings from "../UniklinikBuildings.geojson";
import toiletsPoints from "../toilets.geojson";
import testdata from "../testdata.json";

import roomData from "../Raumplan.geojson";
import EG from "../eg.geojson";

import { Style, Fill, Stroke } from "ol/style";

const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

const center = [771105.02, 6608382.01]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  controls: defaultControls().extend([new FullScreen()]),
  view: new OlView({
    center: center,
    zoom: 0.9,
    maxResolution: 7
  }),
  layers: [stamenLayer]
});

var zoomslider = new ZoomSlider();
map.addControl(zoomslider);

// adding GeoJSON to the map (buildings)
const vector = new OlVector({
  source: new OlVectorSource({
    url: buildingsData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#354d60"
    })
    /* the following few lines of code will make the map buggy
    stroke: new Stroke({
      color: "black",
      width: 0.2
    })
    */
  }),
  minResolution: 0.4
});

map.addLayer(vector);

// adding GeoJSON to the map (rooms)
const vectorTwo = new OlVector({
  source: new OlVectorSource({
    url: roomData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "grey"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(vectorTwo);

// adding GeoJSON to the map (rooms)
const vectorThree = new OlVector({
  source: new OlVectorSource({
    url: klinikumBuildings,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "red"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  minResolution: 0.4
});

map.addLayer(vectorThree);

// adding GeoJSON to the map (rooms eg)
const vectorFour = new OlVector({
  source: new OlVectorSource({
    url: EG,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "blue"
    }),
    stroke: new Stroke({
      color: "black",
      width: 1.2
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(vectorFour);

// adding GeoJSON to the map (toilets)
const toiletsPointsLayer = new OlVector({
  source: new OlVectorSource({
    url: toiletsPoints,
    format: new GeoJSON()
  })
});

map.addLayer(toiletsPointsLayer);

map.on("postcompose", map.updateSize);

class Ebenen extends Component {
  state = {
    show: false,
    color: false
  };

  hideLayer = () => {
    vectorFour.setVisible(false);
  };

  showLayer = () => {
    vectorFour.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  render() {
    return (
      <div id='ContainerEbenen'>
        <button className='EbenenBtn' id='button' onClick={this.toggle}>
          {this.state.show ? this.hideLayer() : this.showLayer()} Etage 2
        </button>
        <button className='EbenenBtn'>Etage 1</button>
      </div>
    );
  }
}

class Displaydata extends Component {
  render() {
    return (
      <div id='displayData'>
        <div id='rowOne' className='rowDisplayData'>
          <p>Gebäudenummer</p>
          <p>Name</p>
          <p>Klinikum/Universität</p>
          <p>Straße</p>
        </div>

        {/* the clicked table rows data from Buildings.js should apear here */}
        <div id='rowTwo' className='rowDisplayData'>
          <p id='Gebäudenummer'></p>
          <p id='Name'></p>
          <p id='Art'></p>
          <p id='Straße'></p>
        </div>
      </div>
    );
  }
}

// when the user moves the mouse, get the name property
// from each feature under the mouse and display it in <DisplayData />
function onMouseMove(browserEvent) {
  var coordinate = browserEvent.coordinate;
  var pixel = map.getPixelFromCoordinate(coordinate);
  var el = document.getElementById("Gebäudenummer");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("GEBNR") + "<br>";
  });

  var el = document.getElementById("Name");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("NAME") + "<br>";
  });

  var el = document.getElementById("Straße");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Straße") + "<br>";
  });

  var el = document.getElementById("Art");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Klinikum/Universität") + "<br>";
  });
}
map.on("click", onMouseMove);

// AgFeatureGrid

const format = new OlFormatGeoJSON();
const features = format.readFeatures(testdata);

// An array of numbers representing an extent: [minx, miny, maxx, maxy].
const extent1 = [988948, 1584461, 50935173, 6953101];

// map class with map component, DisplayData Class, Simple Button and Drawer
class Map extends Component {
  state = { visible: false, showDiv: false, show: false };

  toggleDrawer = () => {
    this.setState({ visible: !this.state.visible });
  };

  toggleSearchbarDiv() {
    this.setState({
      showDiv: !this.state.showDiv
    });
  }

  hideLayer = () => {
    vector.setVisible(false);
  };

  showLayer = () => {
    vector.setVisible(true);
  };

  hideLayerThree = () => {
    vectorThree.setVisible(false);
  };

  showLayerThree = () => {
    vectorThree.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  render() {
    return (
      <div className='App'>
        <Accordion>
          <Card id='LegendeLayerAccordion'>
            <Card.Header>
              <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                Campus Map Köln
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey='0'>
              <Card.Body>
                <div id='containerHintergrund'>
                  <span className='underline'>Hintergrund Karte</span>
                  <br></br>
                  <button id='hintergrundBtnOne'>1</button>
                  <button id='hintergrundBtnTwo'>2</button>
                </div>
                <div id='containerEbenen'>
                  <span className='underline'>Etagen</span>
                  <Ebenen />
                </div>
                <div id='containerLayer'>
                  <span className='underline'>Layer</span>
                  <div id='containerLayerGrid'>
                    <input
                      type='radio'
                      onClick={this.toggle}
                      id='layerVisibilityBtn'
                    >
                      {this.state.show ? this.hideLayer() : this.showLayer()}
                    </input>
                    <div id='blue' onClick={this.showLayer}></div>
                    <div className='LayerLegendeText'>Universität</div>
                  </div>
                  <LayerTransparencySlider
                    layer={vector}
                    className='transparencyVector'
                  />
                </div>

                <div id='containerLayer'>
                  <span className='underline'>Layer</span>
                  <div id='containerLayerGrid'>
                    <input
                      type='radio'
                      onClick={this.toggle}
                      id='layerVisibilityBtn'
                    >
                      {this.state.show
                        ? this.hideLayerThree()
                        : this.showLayerThree()}
                    </input>
                    <div id='red' onClick={this.showLayerThree}></div>
                    <div className='LayerLegendeText'>Uniklinik</div>
                    <LayerTransparencySlider
                      layer={vectorThree}
                      className='transparencyVector'
                    />
                  </div>
                </div>
              </Card.Body>
            </Accordion.Collapse>
          </Card>
        </Accordion>

        <div id='options'>
          <SimpleButton onClick={this.toggleDrawer} icon='bars' id='menuBtn' />
          <ZoomToExtentButton
            id='zoomToExtBtn'
            map={map}
            extent={extent1}
            fitOptions={{
              duration: 3000
            }}
          >
            <img
              src={haus}
              alt='full extend'
              style={{ width: "30px", heigth: "auto" }}
            ></img>
          </ZoomToExtentButton>

          <GeoLocationButton
            onGeolocationChange={() => undefined}
            map={map}
            showMarker={true}
            follow={true}
            id='getlocationbtn'
          >
            <img
              src={getlocation}
              alt='get Location'
              style={{ width: "30px", heigth: "auto" }}
            ></img>
          </GeoLocationButton>
        </div>

        {this.state.showDiv ? (
          <div id='searchBar'>
            <div>Suchen Sie nach Gebäuden etc.</div>
            <input type='text'></input>
          </div>
        ) : null}

        <SimpleButton
          onClick={() => this.toggleSearchbarDiv()}
          id='getSearchBar'
        >
          <img src={search} id='search'></img>
        </SimpleButton>

        {/* DisplayData at the bottom left */}
        <Displaydata />
        {/* MAP */}
        <MapComponent map={map} />
        {/* Button on the left -> opens menu bar on click*/}

        {/* this is the menu bar */}
        <Drawer
          title='Menüleiste'
          placement='left'
          onClose={this.toggleDrawer}
          visible={this.state.visible}
          mask={false}
          width='60vh'
          id='Drawer'
        >
          {/* this is the accordion within the menu bar */}
          <Accordion defaultActiveKey='0'>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Filterfunktionen
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <SimpleButton
                    onClick={() => this.toggleSearchbarDiv()}
                    id='getSearchBarInDrawer'
                  >
                    <img src={search} id='search'></img>
                  </SimpleButton>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Gebäudeliste
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <AgFeatureGrid
                    features={features}
                    map={this.map}
                    columnDefs={{
                      GEN: {
                        headerName: "Name",

                        sortable: true,
                        filter: true,
                        resizable: true
                      },
                      SHAPE_LENG: {
                        headerName: "Length",

                        sortable: true,
                        filter: true,
                        resizable: true
                      },
                      SHAPE_AREA: {
                        headerName: "Area",

                        sortable: true,
                        filter: true,
                        resizable: true
                      }
                    }}
                    zoomToExtent={true}
                    selectable={true}
                  />
                  <span>
                    Klicke <NavLink to='/Buildings'>hier</NavLink>, um zu der
                    ausführlichen Gebäudeübersicht zu gelangen.
                  </span>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Links
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <ul>
                    <li>
                      <NavLink
                        to='/Buildings'
                        target='_blank'
                        id='gebauelisteLink'
                      >
                        Gebäudeliste
                      </NavLink>
                    </li>
                    <li>
                      <a
                        href='http://www.uni-koeln.de/uni/gebaeude/liste.html'
                        target='_blank'
                        id='gebauelisteLink'
                      >
                        Lagepläne der Uniklinik
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://verwaltung.uni-koeln.de/abteilung54/content/sachgebiet_4/parkraumbewirtschaftung/index_ger.html'
                        target='_blank'
                        id='gebauelisteLink'
                      >
                        Parken an der Uni
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://www.kvb.koeln/'
                        target='_blank'
                        id='gebauelisteLink'
                      >
                        Liniennetzplan der KVB
                      </a>
                    </li>
                    <li>
                      <a
                        href='http://www.portal.uni-koeln.de/oeffnungszeiten_adr.html#c36618'
                        target='_blank'
                        id='gebauelisteLink'
                      >
                        Öffnungszeiten zentrale Gebäude
                      </a>
                    </li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Folge der Universität zu Köln!
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <a
                    href='https://www.facebook.com/universitaetkoeln/'
                    target='_blank'
                    className='fa fa-facebook'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.instagram.com/uni_koeln/?hl=de'
                    target='_blank'
                    className='fa fa-instagram'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.youtube.com/user/UniversitaetzuKoeln'
                    target='_blank'
                    className='fa fa-youtube'
                    id='socialMediaBtn'
                  ></a>
                  <ul>
                    <li>
                      <a
                        href='https://www.uni-koeln.de/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Universität zu Köln
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://www.geographie.uni-koeln.de/'
                        target='_blank'
                        id='geoInstitutLink'
                      >
                        Geographisches Institut
                      </a>
                    </li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <img src={uniSchriftzug} alt='logo' id='logoSchriftzug' />
        </Drawer>
      </div>
    );
  }
}

export default Map;
