import React, { Component } from "react";

import { NavLink } from "react-router-dom";

import { reveal as Menu } from "react-burger-menu";
import { Accordion } from "react-bootstrap";

class Navigation extends Component {
  state = {
    visible: true,
    buttonVisible: false
  };

  hideNavbar = () => {
    this.setState({
      visible: false
    });
  };

  showNavbar = () => {
    this.setState({
      visible: true
    });
  };

  render() {
    return (
      <div>
        <Accordion defaultActiveKey='0'>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                Filterfunktionen
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey='0'>
              <Card.Body>Hier werden Filterfunktionen angezeigt</Card.Body>
            </Accordion.Collapse>
          </Card>
        </Accordion>
        <span
          id='NavbarOnOffText'
          style={{ top: this.state.visible ? "68px" : "10px" }}
        >
          Navigationsleiste ein- oder ausblenden
        </span>
        <button
          onClick={this.showNavbar}
          id='showNavbarBtn'
          style={
            ({ display: this.state.visible ? "none" : "block" },
            { top: this.state.visible ? "95px" : "40px" })
          }
        ></button>
        <div
          id='globalnav'
          style={{ display: this.state.visible ? "block" : "none" }}
        >
          <div className='hideNavbarBtn'>
            <button onClick={this.hideNavbar}></button>
          </div>

          <div id='gnavLogo'>
            <p>
              <a href='Map.js'>Universität zu Köln</a>
            </p>
          </div>

          <NavLink to='/Kontakt'>Kontakt</NavLink>
          <NavLink to='/Infos'>Infos</NavLink>
          <NavLink to='/Buildings'>Gebäudeübersicht</NavLink>
          <NavLink to='/'>Lageplan</NavLink>
          <Menu right width={"100%"}>
            <a id='home' className='menu-item' href='/'>
              Lageplan
            </a>
            <a id='about' className='menu-item' href='/about'>
              Gebäudeliste
            </a>
            <a id='contact' className='menu-item' href='/contact'>
              Infos
            </a>
            <a className='menu-item--small' href=''>
              Kontakt
            </a>
          </Menu>
        </div>
      </div>
    );
  }
}

export default Navigation;
