    this.setState({
      showDiv: !this.state.showDiv
    });
  }
  render() {
    return (
      <div>
        {this.state.showDiv ? (
          <div id='searchBar'>
            <div>Suchen Sie nach Geb√§uden etc.</div>
            <input type='text'></input>
          </div>
        ) : null}

        <SimpleButton
          onClick={() => this.toggleSearchbarDiv()}
          id='getSearchBar'
        >
          <img src={search} id='search'></img>
        </SimpleButton>
      </div>
    );
  }
}

export default Searchbar;
