import React, { Component } from "react";

class MobileNav extends Component {
  state = {
    visible: true
  };

  hideNavBar = () => {
    this.setState({
      visible: false
    });
  };

  showNavBar = () => {
    this.setState({
      visible: true
    });
  };
  render() {
    return (
      <div>
        <div
          id='legende'
          style={{ display: this.state.visible ? "block" : "none" }}
        >
          <button onClick={this.hideLegende}>Legende ausblenden</button>
          <div id='legendeGrid'>
            <img src={barrierfreeEntrance} className='legendeLogo'></img>
            <p className='legendeText'>Barrierefreier Zugang</p>
            <img src={bushaltestelle} className='legendeLogo'></img>
            <p className='legendeText'>KvB-Haltestellen</p>
            <img src={regionalerVerkehr} className='legendeLogo'></img>
            <p className='legendeText'>regionaler Verkehr</p>
          </div>
        </div>
        <button
          onClick={this.showLegende}
          id='legendeShow'
          style={{ display: this.state.visible ? "none" : "block" }}
        >
          Legende anzeigen
        </button>
      </div>
    );
  }
}

export default MobileNav;
