import React, { Component } from "react";

class Legende extends Component {
  state = {
    visible: true
  };

  hideLegende = () => {
    this.setState({
      visible: false
    });
  };

  showLegende = () => {
    this.setState({
      visible: true
    });
  };

  render() {
    return (
      <div>
        <div
          id='legende'
          style={{ display: this.state.visible ? "block" : "none" }}
        >
          <button onClick={this.hideLegende}>Legende ausblenden</button>
          <div id='legendeGrid'>
            <img src='../src/img/barrierfreeEntrance.jpeg'></img>
            <p>Beschreibung</p>
            <p>Signatur</p>
            <p>Beschreibung</p>
            <p>Signatur</p>
            <p>Beschreibung</p>
          </div>
        </div>
        <button
          onClick={this.showLegende}
          id='legendeShow'
          style={{ display: this.state.visible ? "none" : "block" }}
        >
          Legende anzeigen
        </button>
      </div>
    );
  }
}

export default Legende;
