import React, { Component } from "react";
import parkingOeffentlich from "../img/parking-öffentlich.png";
import parkingUni from "../img/parking-uni.png";
import parkingKlinik from "../img/parking-klinik.png";
import wickelraum from "../img/wickelraum.png";
import stillraum from "../img/stillraum.png";
import elternKindRaum from "../img/eltern-kind-raum.png";
import kita from "../img/kita.png";
import spielplatz from "../img/spielplatz.png";
import unisex from "../img/unisex.png";

class Legende extends Component {
  state = {
    show: false
  };

  hideLayer = () => {
    vector.setVisible(false);
  };

  showLayer = () => {
    vector.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  // <input type='radio' onClick={this.toggle} id='layerVisibilityBtn'>
  //{this.state.show ? this.hideLayer() : this.showLayer()}
  // </input>

  render() {
    return (
      <div>
        <div id='legende'>
          <button onClick={this.toggle}>Legende ausblenden</button>
          <div id='legendeGrid'>
            <p className='gridFullWidth'>Parkplätze</p>
            <img
              className='legendeLogo'
              src={parkingOeffentlich}
              alt='parking points'
            ></img>
            <p className='legendeText'>öffentlich</p>
            <img
              className='legendeLogo'
              src={parkingUni}
              alt='parking points'
            ></img>
            <p className='legendeText'>Uni. angeh.</p>
            <img
              className='legendeLogo'
              src={parkingKlinik}
              alt='parking points'
            ></img>
            <p className='legendeText'>Klinik</p>

            <p className='gridFullWidth'>Familien-Campus-Plan</p>
            <img
              className='legendeLogo'
              src={wickelraum}
              alt='wickelraum points'
            ></img>
            <p className='legendeText'>Wickelraum</p>
            <img
              className='legendeLogo'
              src={stillraum}
              alt='stillraum points'
            ></img>
            <p className='legendeText'>Stillraum</p>
            <img
              className='legendeLogo'
              src={elternKindRaum}
              alt='elternKindRaum points'
            ></img>
            <p className='legendeText'>Eltern-Kind-Raum</p>
            <img className='legendeLogo' src={kita} alt='kita points'></img>
            <p className='legendeText'>Kindertagesstätte</p>
            <img
              className='legendeLogo'
              src={spielplatz}
              alt='spielplatz points'
            ></img>
            <p className='legendeText'>Spielplatz</p>
            <img
              className='legendeLogo'
              src={unisex}
              alt='unisex toilets points'
            ></img>
            <p className='legendeText'>Unisex-WC</p>
          </div>
        </div>
      </div>
    );
  }
}

export default Legende;
