import React, { Component } from "react";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";
import OlMap from "ol/Map";
import OlView from "ol/View";
import OlVector from "ol/layer/Vector";
import OlVectorSource from "ol/source/Vector";
import GeoJSON from "ol/format/GeoJSON";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";

import { Drawer } from "antd";
import {
  SimpleButton,
  MapComponent,
  NominatimSearch
} from "@terrestris/react-geo";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";

import buildingsData from "../campusGisBuildings.geojson";
import klinikumBuildings from "../UniklinikBuildings.geojson";
import roomData from "../Raumplan.geojson";
import { Style, Fill, Stroke, Text } from "ol/style";
import Legende from "../components/Legende.js";
import Geolocation from "../components/Geolocation.js";

const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

const center = [772305.02, 6608382.01]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  view: new OlView({
    center: center,
    zoom: 0.9,
    maxResolution: 14
  }),
  layers: [stamenLayer]
});

// adding GeoJSON to the map (buildings)
const vector = new OlVector({
  source: new OlVectorSource({
    url: buildingsData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#354d60"
    }),
    style: function(feature) {
      style.getText().setText(feature.get("name"));
      return style;
    }
  }),
  minResolution: 0.4
});

map.addLayer(vector);

// adding GeoJSON to the map (rooms)
const vectorTwo = new OlVector({
  source: new OlVectorSource({
    url: roomData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "grey"
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(vectorTwo);

// adding GeoJSON to the map (rooms)
const vectorThree = new OlVector({
  source: new OlVectorSource({
    url: klinikumBuildings,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "red"
    })
  }),
  minResolution: 0.4
});

map.addLayer(vectorThree);

map.on("postcompose", map.updateSize);

// displays selected buildings data (ol example try)
const style = new Style({
  fill: new Fill({
    color: "rgba(255, 255, 255, 0.6)"
  }),
  stroke: new Stroke({
    color: "#319FD3",
    width: 1
  }),
  text: new Text({
    font: "12px Calibri,sans-serif",
    fill: new Fill({
      color: "#000"
    }),
    stroke: new Stroke({
      color: "#fff",
      width: 3
    })
  })
});

const highlightStyle = new Style({
  stroke: new Stroke({
    color: "#f00",
    width: 1
  }),
  fill: new Fill({
    color: "rgba(255,0,0,0.1)"
  }),
  text: new Text({
    font: "12px Calibri,sans-serif",
    fill: new Fill({
      color: "#000"
    }),
    stroke: new Stroke({
      color: "#f00",
      width: 3
    })
  })
});

const featureOverlay = new OlVector({
  source: new OlVectorSource(),
  map: map,
  style: function(feature) {
    highlightStyle.getText().setText(feature.get("name"));
    return highlightStyle;
  }
});

var highlight;
var displayFeatureInfo = function(pixel) {
  var feature = map.forEachFeatureAtPixel(pixel, function(feature) {
    return feature;
  });

  var info = document.getElementById("info");
  if (feature) {
    info.innerHTML = feature.getId() + ": " + feature.get("id");
  } else {
    info.innerHTML = "&nbsp;";
  }

  if (feature !== highlight) {
    if (highlight) {
      featureOverlay.getSource().removeFeature(highlight);
    }
    if (feature) {
      featureOverlay.getSource().addFeature(feature);
    }
    highlight = feature;
  }
};

map.on("pointermove", function(evt) {
  if (evt.dragging) {
    return;
  }
  var pixel = map.getEventPixel(evt.originalEvent);
  displayFeatureInfo(pixel);
});

map.on("click", function(evt) {
  displayFeatureInfo(evt.pixel);
});

