import React, { Component } from "react";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";

import OlMap from "ol/Map";
import OlView from "ol/View";
import OlSize from "ol/size";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
import OlSourceWMTS from "ol/source/WMTS";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";

import { Drawer } from "antd";
import { SimpleButton, MapComponent } from "@terrestris/react-geo";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card } from "react-bootstrap";

const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

const center = [788453.4890155146, 6573085.729161344]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  view: new OlView({
    center: center,
    zoom: 5
  }),
  layers: [stamenLayer]
});

map.on("postcompose", map.updateSize);

class Map extends Component {
  state = { visible: false };

  toggleDrawer = () => {
    this.setState({ visible: !this.state.visible });
  };

  render() {
    return (
      <div className='App'>
        <MapComponent map={map} />
        <SimpleButton
          style={{ position: "fixed", top: "66px", left: "45px" }}
          onClick={this.toggleDrawer}
          icon='bars'
        />
        <Drawer
          title='Menüleiste'
          placement='left'
          onClose={this.toggleDrawer}
          visible={this.state.visible}
          mask={false}
          width='60vh'
        >
          <input type='text' placeholder='Suchen...'></input>
          <input type='submit'></input>
          <Card id='menüCard'>
            <p>Hier dann Suchfunktionen einbinden</p>
            <p>Gebäudeliste</p>
            <p>Links</p>
          </Card>

          <img
            src='https://kommunikation-marketing.uni-koeln.de/e135228/e136450/e136573/e136614/e136634/013_Unisiegel_plus_Schriftzug_DE_ger.jpg'
            id='logoSchriftzug'
          ></img>
        </Drawer>
      </div>
    );
  }
}

export default Map;
