import React, { Component } from "react";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";

import OlMap from "ol/Map";
import OlView from "ol/View";
import OlSize from "ol/size";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
import OlSourceWMTS from "ol/source/WMTS";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";
import GeoJSON from "ol/format/GeoJSON";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";

import { Drawer } from "antd";
import { SimpleButton, MapComponent } from "@terrestris/react-geo";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";

// MAP
const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

// dieser Layer wird nicht auf der Karte angezeigt
const gebäudeLayer = new VectorLayer({
  source: new VectorSource({
    url: "./gebäudeLayer.geojson",
    format: new GeoJSON()
  })
});

const center = [772305.02, 6608382.01]; //Cologne

const map = new OlMap({
  view: new OlView({
    center: center,
    zoom: 14
  }),
  layers: [stamenLayer, gebäudeLayer]
});

map.on("postcompose", map.updateSize);
// ENDE MAP

class Displaydata extends Component {
  render() {
    return (
      <div id='displayData'>
        <div id='rowOne' class='rowDisplayData'>
          <p>Gebäudenummer</p>
          <p>Klinikum/Universität</p>
          <p>Name/Nutzer</p>
          <p>Straße</p>
        </div>
        <div id='rowTwo' class='rowDisplayData'>
          <p id='Gebäudenummer'>1</p>
          <p id='Art'>Klinikum</p>
          <p id='Name'>Klinikum-Verwaltung</p>
          <p id='Straße'>Kerpener Str. 68</p>
        </div>
      </div>
    );
  }
}

class Map extends Component {
  state = { visible: false };

  toggleDrawer = () => {
    this.setState({ visible: !this.state.visible });
  };

  render() {
    return (
      <div className='App'>
        {/* Info Container unten links */}
        <Displaydata />

        {/* MAP */}
        <MapComponent map={map} />

        {/* Button on the left */}
        <SimpleButton
          style={{ position: "fixed", top: "66px", left: "45px" }}
          onClick={this.toggleDrawer}
          icon='bars'
        />

        {/* This is the Menu bar */}
        <Drawer
          title='Menüleiste'
          placement='left'
          onClose={this.toggleDrawer}
          visible={this.state.visible}
          mask={false}
          width='60vh'
        >
          <input
            type='text'
            placeholder='Straßen- oder Gebäudename...'
            id='inputSearch'
          ></input>
          <input type='submit' value='Suchen' id='form-btn'></input>

          {/* This is the Accordion function within the menu bar */}
          <Accordion defaultActiveKey='0'>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Filterfunktionen
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>Hier werden Filterfunktionen angezeigt.</Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Gebäudeliste
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  Klicke <NavLink to='/Gebäudeliste'>hier</NavLink>, um zu der
                  Gebäudeübersicht zu gelangen.
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Links
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <ul>
                    <li>
                      <NavLink to='/Gebäudeliste' target='_blank'>
                        Gebäudeliste
                      </NavLink>
                    </li>
                    <li>
                      <a
                        href='https://www.uk-koeln.de/uniklinik-koeln/lageplan-anfahrt/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Lagepläne der Uniklinik
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://verwaltung.uni-koeln.de/abteilung54/content/sachgebiet_4/parkraumbewirtschaftung/index_ger.html'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Parken an der Universität
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://www.kvb.koeln/german/fahrplan/linienplan.html'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Liniennetzplan der KVB
                      </a>
                    </li>
                    <li>
                      <a
                        href='http://www.portal.uni-koeln.de/oeffnungszeiten_adr.html#c36618'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Öffnungszeiten zentraler Gebäude
                      </a>
                    </li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <Accordion>
            <Card>
              <Card.Header>
                <Accordion.Toggle as={Button} variant='link' eventKey='0'>
                  Folge der Universität zu Köln!
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey='0'>
                <Card.Body>
                  <a
                    href='https://www.facebook.com/universitaetkoeln/'
                    target='_blank'
                    class='fa fa-facebook'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.instagram.com/uni_koeln/?hl=de'
                    target='_blank'
                    class='fa fa-instagram'
                    id='socialMediaBtn'
                  ></a>
                  <a
                    href='https://www.youtube.com/user/UniversitaetzuKoeln'
                    target='_blank'
                    class='fa fa-youtube'
                    id='socialMediaBtn'
                  ></a>
                  <ul>
                    <li>
                      <a
                        href='https://www.uni-koeln.de/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Universität zu Köln
                      </a>
                    </li>
                    <li>
                      <a
                        href='https://www.geographie.uni-koeln.de/'
                        target='_blank'
                        id='uniKoelnLink'
                      >
                        Geographisches Institut
                      </a>
                    </li>
                  </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <img src={uniSchriftzug} alt='logo' id='logoSchriftzug' />
        </Drawer>
      </div>
    );
  }
}

export default Map;
