import React, { Component } from "react";
import * as PropTypes from "prop-types";

import "ol/ol.css";
import "antd/dist/antd.css";
//import "../react-geo.css";
import OlMap from "ol/Map";
import OlView from "ol/View";
import OlVector from "ol/layer/Vector";
import OlVectorSource from "ol/source/Vector";
import GeoJSON from "ol/format/GeoJSON";
import OlLayerTile from "ol/layer/Tile";
import OlSourceOsm from "ol/source/OSM";
//import Projection from "ol/proj/Projection";
import OlStamen from "ol/source/Stamen";
import { Drawer } from "antd";
import { SimpleButton, MapComponent } from "@terrestris/react-geo";

import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion, Button } from "react-bootstrap";

import { NavLink } from "react-router-dom";

import uniSchriftzug from "../img/uniSchriftzug.jpg";

import buildingsData from "../campusGisBuildings.geojson";
import klinikumBuildings from "../UniklinikBuildings.geojson";
import roomData from "../Raumplan.geojson";
import { Style, Fill, Stroke } from "ol/style";
import Legende from "../components/Legende.js";

const layer = new OlLayerTile({
  source: new OlSourceOsm()
});

const stamenLayer = new OlLayerTile({
  source: new OlStamen({
    layer: "terrain"
  })
});

const center = [772305.02, 6608382.01]; //Cologne

// create a new instance of ol.map in ES6 syntax
const map = new OlMap({
  view: new OlView({
    center: center,
    zoom: 0.9,
    maxResolution: 14
  }),
  layers: [stamenLayer]
});

// adding GeoJSON to the map (buildings)
const vector = new OlVector({
  source: new OlVectorSource({
    url: buildingsData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "#354d60"
    })
  }),
  minResolution: 0.4
});

map.addLayer(vector);

// adding GeoJSON to the map (rooms)
const vectorTwo = new OlVector({
  source: new OlVectorSource({
    url: roomData,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "grey"
    })
  }),
  maxResolution: 0.39999999
});

map.addLayer(vectorTwo);

// adding GeoJSON to the map (rooms)
const vectorThree = new OlVector({
  source: new OlVectorSource({
    url: klinikumBuildings,
    format: new GeoJSON()
  }),
  style: new Style({
    fill: new Fill({
      color: "red"
    })
  }),
  minResolution: 0.4
});

map.addLayer(vectorThree);

map.on("postcompose", map.updateSize);

class Ebenen extends Component {
  render() {
    return (
      <div id='ContainerEbenen'>
        <button className='EbenenBtn'>2</button>
        <button className='EbenenBtn'>1</button>
        <button className='EbenenBtn'>EG</button>
      </div>
    );
  }
}

// displays selected buildings data (not programmed yet)
class Displaydata extends Component {
  render() {
    return (
      <div id='displayData'>
        <div id='rowOne' className='rowDisplayData'>
          <p>Gebäudenummer</p>
          <p>Name</p>
          <p>Klinikum/Universität</p>
          <p>Straße</p>
        </div>

        {/* the clicked table rows data from Buildings.js should apear here */}
        <div id='rowTwo' className='rowDisplayData'>
          <p id='Gebäudenummer'></p>
          <p id='Name'></p>
          <p id='Art'></p>
          <p id='Straße'></p>
        </div>
      </div>
    );
  }
}

// displays layer
class LegendeLayer extends Component {
  state = {
    show: false
  };

  hideLayer = () => {
    vector.setVisible(false);
  };

  showLayer = () => {
    vector.setVisible(true);
  };

  hideLayerThree = () => {
    vectorThree.setVisible(false);
  };

  showLayerThree = () => {
    vectorThree.setVisible(true);
  };

  toggle = () => this.setState(currentState => ({ show: !currentState.show }));

  render() {
    return (
      <div>
        <div id='gridLayerLegende'>
          <button onClick={this.toggle} id='layerVisibilityBtn'>
            {this.state.show ? this.hideLayer() : this.showLayer()}
          </button>
          <div id='blue' onClick={this.showLayer}></div>
          <div className='LayerLegendeText'>Universität</div>

          <button onClick={this.toggle} id='layerVisibilityBtn'>
            {this.state.show ? this.hideLayerThree() : this.showLayerThree()}
          </button>
          <div id='red' onClick={this.showLayerThree}></div>
          <div className='LayerLegendeText'>Uniklinik</div>
        </div>
      </div>
    );
  }
}

// when the user moves the mouse, get the name property
// from each feature under the mouse and display it in <DisplayData />
function onMouseMove(browserEvent) {
  var coordinate = browserEvent.coordinate;
  var pixel = map.getPixelFromCoordinate(coordinate);
  var el = document.getElementById("Gebäudenummer");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("GEBNR") + "<br>";
  });

  var el = document.getElementById("Name");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("NAME") + "<br>";
  });

  var el = document.getElementById("Straße");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Straße") + "<br>";
  });

  var el = document.getElementById("Art");
  el.innerHTML = "";
  map.forEachFeatureAtPixel(pixel, function(feature) {
    el.innerHTML += feature.get("Klinikum/Universität") + "<br>";
  });
}
map.on("click", onMouseMove);

// select features
const selectSingleClick = new Select();

var changeInteraction = function() {
  if (select !== null) {
    map.removeInteraction(select);
  }
  var value = selectElement.value;
  if (value == "singleclick") {
    select = selectSingleClick;
  } else if (value == "click") {
    select = selectClick;
  } else if (value == "pointermove") {
    select = selectPointerMove;
  } else if (value == "altclick") {
    select = selectAltClick;
  } else {
    select = null;
  }
  if (select !== null) {
    map.addInteraction(select);
    select.on("select", function(e) {
      document.getElementById("status").innerHTML =
        "&nbsp;" +
        e.target.getFeatures().getLength() +
        " selected features (last operation selected " +
        e.selected.length +
        " and deselected " +
        e.deselected.length +
        " features)";
    });
  }
};

