import React, { Component } from "react";

import { NavLink } from "react-router-dom";

import { reveal as Menu } from "react-burger-menu";

import { SimpleButton } from "@terrestris/react-geo";
import MobileNav from "../components/MobileNav.js";

class Navigation extends Component {
  state = {
    visible: true,
    buttonVisible: false
  };

  hideNavbar = () => {
    this.setState({
      visible: false
    });
  };

  showNavbar = () => {
    this.setState({
      visible: true
    });
  };

  render() {
    return (
      <div>
        <SimpleButton
          tooltip='show/hide navigationbar'
          tooltipPlacement='left'
          onClick={this.showNavbar}
          id='showNavbarBtn'
          className='tooltiptext'
          style={
            ({ display: this.state.visible ? "none" : "block" },
            { top: this.state.visible ? "65px" : "8px" })
          }
        />

        <div
          id='globalnav'
          style={{ display: this.state.visible ? "block" : "none" }}
        >
          <MobileNav id='MobileNav' />

          <div className='hideNavbarBtn'>
            <SimpleButton
              tooltip='show/hide navigationbar'
              tooltipPlacement='left'
              onClick={this.hideNavbar}
            />
          </div>

          <div id='gnavLogo'>
            <a href='Map.js'>
              Universität <span>zu Köln</span>
            </a>
            <span>hello</span>
          </div>

          <NavLink to='/Kontakt'>Kontakt</NavLink>
          <NavLink to='/Infos'>Infos</NavLink>
          <NavLink to='/Buildings'>Gebäudeübersicht</NavLink>
          <NavLink to='/'>Lageplan</NavLink>
          <Menu right width={"100%"}>
            <a id='home' className='menu-item' href='/'>
              Lageplan
            </a>
            <a id='about' className='menu-item' href='/about'>
              Gebäudeliste
            </a>
            <a id='contact' className='menu-item' href='/contact'>
              Infos
            </a>
            <a className='menu-item--small' href=''>
              Kontakt
            </a>
          </Menu>
        </div>
      </div>
    );
  }
}

export default Navigation;
